import { mapGetters, mapMutations } from 'vuex'

export default {
  name: 'ShipControls',
  props: {
    ship: Object,
  },

  computed: {
    ...mapGetters([
      'isControlDisabled',
    ]),
  },

  methods: {
    ...mapMutations([
      'moveShipTiles',
      'reduceMovesAvailable',
    ]),

    moveShip: function (row, col, size, direction) {
      // move ship tiles
      this.moveShipTiles ({row: row, col: col, size: size, direction: direction})
      this.reduceMovesAvailable()
    },
  }

}
