import { mapGetters, mapMutations } from 'vuex'

export default {
  name: 'CharacterBar',

  computed: {
    ...mapGetters([
      'getCurrentPlayerName',
      'getCurrentAvatar',
      'getMovesLeft',
      'getShotsLeft',
    ]),
  },

  methods: {
    ...mapMutations([
      'setShipType',
      'updateCurrentPlayerName',
      'toggleSettings',
    ]),
    updatePlayerName (e) {
      this.updateCurrentPlayerName(e)
    },
  }

}
